using System.Web.UI.WebControls;

namespace NucleusExams.DemoPages
{
    public partial class DrawingTools : System.Web.UI.Page
    {
        ExamController objExam = new ExamController();
        protected void Page_Load(object sender, EventArgs e)
        {
            if (!IsPostBack)
            {
                if (Session["EID"] != null && Session["SID"] != null && Request.QueryString["QID"] != null)
                {
                    string Answer = objExam.GetAnswerByExamIDStudentIDQuestionID(Convert.ToDecimal(Session["EID"]), Convert.ToDecimal(Session["SID"]), Request.QueryString["QID"].ToString());
                    if (Answer != "")
                    {
                        string NewImage = Answer.Substring(10);
                        NewImage = NewImage.Remove(NewImage.Length - 2);
                        editor.ImageUrl = NewImage;
                    }
                    //else
                    //{
                    //    string defaultImageUrl = "data:image/png;base64,";
                    //    editor.ImageUrl = defaultImageUrl;
                    //}
                }
                //else
                //{
                //    string defaultImageUrl = "data:image/png;base64,";
                //    editor.ImageUrl = defaultImageUrl;
                //}
            }


        }

        protected void btnReset_Click(object sender, EventArgs e)
        {
            string defaultImageUrl = "data:image/png;base64,";
            editor.ImageUrl = defaultImageUrl;
        }
    }
}